CREATE DATABASE crypto_db;

CREATE EXTERNAL DATA SOURCE crypto_dls
WITH ( 
    LOCATION = 'abfss://gold@cryptopipestorage.dfs.core.windows.net' 
);

CREATE EXTERNAL FILE FORMAT ParquetFormat
WITH (
    FORMAT_TYPE = PARQUET
);

CREATE OR ALTER VIEW dbo.vw_gold_coins_raw AS
SELECT *
FROM OPENROWSET(
    BULK 'coins/*/*/*/*',
    DATA_SOURCE = 'crypto_dls',
    FORMAT = 'PARQUET'
) AS rows;
GO

CREATE OR ALTER VIEW dbo.vw_gold_coins AS
SELECT
    r.*,
    j.[times]      AS roi_times,
    j.[currency]   AS roi_currency,
    j.[percentage] AS roi_percentage
FROM dbo.vw_gold_coins_raw r
OUTER APPLY OPENJSON(TRY_CAST(r.roi AS NVARCHAR(MAX)))
WITH (
    [times] FLOAT        '$.times',
    [currency] NVARCHAR(50) '$.currency',
    [percentage] FLOAT   '$.percentage'
) j;
GO


SELECT TOP 10 * FROM dbo.vw_gold_coins where atl_change_percentage is NULL;
